package com.class2;

public class arithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=30;
		int b=15;
		System.out.println(a+b);  //45
		System.out.println(a-b);  //15
		System.out.println(a*b);  //450
		System.out.println(a/b);  //2
		
		int sum=a+b;
		int sub=a-b;
		int mult=a*b;
		int div=a/b;
		
		System.out.println(sum);  //45
		System.out.println(sub);  //15
		System.out.println(mult);  //450
		System.out.println(div);  //2
		
		//print: the Addition/Subtraction.. __ of 2 number is __
		
		System.out.println("the sum of 2 number is "+sum);
		System.out.println("the sub of 2 number is "+sub);
		System.out.println("the mult of 2 number is "+mult);
		System.out.println("the div of 2 number is "+div);
		
		
		
		
		
	}

}
